mapper.select:
SELECT a.`name` AS agencyName, s.result_name AS resultName, s.result_grade AS resultGrade, s.item FROM supervise_record AS s LEFT JOIN org_agency AS a ON s.agency_id = a.id WHERE a.is_delete = 0
AND a.id = #{agencyId}
AND s.result_grade = #{grade}

SELECT r.result_grade as resultGrade, a.name as agencyName, a.social_credit_code as socialCreditCode, a.address as address, a.charge_person as chargePerson, a.contact_way as contactWay, a.agency_type as agencyType, a.register_time as registerTime, a.business_scope as businessScope FROM ( SELECT * FROM supervise_record AS r WHERE NOT EXISTS ( SELECT 1 FROM supervise_record WHERE agency_id = r.agency_id AND result_grade > r.result_grade ) ) AS r LEFT JOIN org_agency AS a ON r.agency_id = a.id WHERE a.is_delete = 0
AND r.result_grade = #{query.resultGrade}
order by r.result_grade
