mapper.select:
SELECT a.`name` AS agencyName, s.result_name AS item, s.result_grade AS resultGrade, s.item, s.create_time AS createTime FROM supervise_record AS s LEFT JOIN org_agency AS a ON s.agency_id = a.id WHERE a.is_delete = 0
AND a.id = #{agencyId}
AND s.result_grade = #{grade}

SELECT resultGrade, a.name as agencyName, a.social_credit_code as socialCreditCode, a.address as address, a.charge_person as chargePerson, a.contact_way as contactWay, a.agency_type as agencyType, a.register_time as registerTime, a.business_scope as businessScope, a.agency_coordinate as agencyCoordinate FROM ( SELECT MAX(result_grade) AS resultGrade, agency_id AS eId FROM supervise_record AS e INNER JOIN ( SELECT MIN(create_time) AS min_time, agency_id AS cId FROM supervise_record c WHERE `status` = 1 AND target_type = 1 GROUP BY agency_id ) AS b on e.agency_id = b.cId WHERE `status` = 1 AND create_time >= IF ( b.min_time < DATE_SUB(CURDATE(), INTERVAL 1 YEAR), DATE_SUB(CURDATE(), INTERVAL 1 YEAR), b.min_time ) AND create_time < CURDATE() GROUP BY e.agency_id ) AS r LEFT JOIN org_agency AS a ON r.eId = a.id WHERE a.is_delete = 0
AND resultGrade = #{query.resultGrade}
order by resultGrade

SELECT r.id, r.target_type AS targetType, r.personnel_name AS relatedPersonnelName, r.item, r.code, r.status, r.is_wjs AS isWjs, r.result_name AS item, r.result_grade AS resultGrade, a.`name` AS agencyName, p.`name` AS personnelName FROM supervise_record AS r LEFT JOIN org_agency AS a ON r.agency_id = a.id LEFT JOIN org_personnel AS p ON r.personnel_id = p.id WHERE 1 = 1
AND r.item LIKE CONCAT('%',#{query.item},'%')
AND r.target_type = #{query.targetType}
AND r.result_grade = #{query.resultGrade}
AND a.name LIKE CONCAT('%',#{query.agencyName},'%')
AND a.id = #{query.agencyId}
AND p.name LIKE CONCAT('%',#{query.personnelName},'%')
AND r.personnel_name LIKE CONCAT('%',#{query.relatedPersonnelName},'%')
AND r.create_time >= #{query.bgTime}
AND r.create_time <= #{query.endTime}
AND r.status = #{query.status}
AND r.is_wjs = #{query.isWjs}
AND r.code = #{query.code}

SELECT Count( a.result_grade = 1 OR NULL ) AS grade1, Count( a.result_grade = 2 OR NULL ) AS grade2, Count( a.result_grade = 3 OR NULL ) AS grade3, Count( a.result_grade = 4 OR NULL ) AS grade4, Count( a.result_grade = 5 OR NULL ) AS grade5 FROM supervise_record a INNER JOIN ( SELECT MIN( create_time ) AS time, agency_id AS b_agencyId FROM supervise_record WHERE `status` = 1 AND target_type = 1 GROUP BY agency_id ) b ON a.agency_id = b.b_agencyId WHERE a.`status` = 1 AND a.create_time < CURRENT_DATE ( ) AND a.create_time >= IF ( TIMESTAMPDIFF( DAY, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ), b.time ) > 0, b.time, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ) )
AND date_format(a.create_time,'%Y-%m-%d') >= #{bgTime}
AND date_format(a.create_time,'%Y-%m-%d') <=#{endTime}

SELECT Count( c.mgrade = 1 OR NULL ) AS grade1, Count( c.mgrade = 2 OR NULL ) AS grade2, Count( c.mgrade = 3 OR NULL ) AS grade3, Count( c.mgrade = 4 OR NULL ) AS grade4, Count( c.mgrade = 5 OR NULL ) AS grade5 FROM ( SELECT max( a.result_grade ) AS mgrade, IF ( TIMESTAMPDIFF( DAY, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ), b.time ) > 0, b.time, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ) ) AS bgtime FROM supervise_record a INNER JOIN ( SELECT MIN( create_time ) AS time, agency_id AS b_agencyId FROM supervise_record WHERE `status` = 1 AND target_type = 1 GROUP BY agency_id ) b ON a.agency_id = b.b_agencyId WHERE a.`status` = 1 AND a.create_time < CURRENT_DATE ( ) AND a.create_time >= IF ( TIMESTAMPDIFF( DAY, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ), b.time ) > 0, b.time, DATE_SUB( CURRENT_DATE ( ), INTERVAL 1 YEAR ) )
AND date_format(a.create_time,'%Y-%m-%d') >= #{bgTime}
AND date_format(a.create_time,'%Y-%m-%d') <=#{endTime}
GROUP BY a.agency_id ) c
