mapper.select:
SELECT a.id, o.id AS agencyId, o.name AS agencyName, t.id AS itemId, t.item_name AS itemName, t.item_target_type AS itemTargetType FROM org_agency AS o LEFT JOIN template_item_agency AS a ON o.id = a.agency_id LEFT JOIN template_item AS t ON a.item_id = t.id WHERE o.is_delete = 0
AND o.id = #{query.agencyId}
AND t.item_name = #{query.itemName}
AND t.item_target_type = #{query.itemTargetType}

SELECT
i.id AS itemId,
i.item_name AS itemName,
i.item_target_type AS itemTargetType
FROM
template_item AS i
LEFT JOIN ( SELECT item_id, agency_id FROM template_item_agency WHERE 1 =1

    AND template_item_agency.agency_id = #{query.agencyId}

) a ON i.id = a.item_id
WHERE
a.item_id IS NULL

    AND i.item_name = #{query.itemName}


    AND i.item_target_type = #{query.itemTargetType}
